using UnityEngine;using System.Collections.Generic;namespace Common{    public class ResourceManager : Singleton<ResourceManager>    {        private Dictionary<string, GameObject> dicResources;        public override void Init()        {            dicResources = new Dictionary<string, GameObject>();        }        public override void Release()        {            dicResources.Clear();            Resources.UnloadUnusedAssets();        }        public GameObject GetResouce(string path)        {            GameObject go = null;            if(!dicResources.ContainsKey(path))            {                go = Resources.Load(path) as GameObject;                if(go != null)                {                    dicResources[path] = go;                }            }            else            {                go = dicResources[path];            }            if (go != null)            {                GameObject ret = GameObject.Instantiate(go) as GameObject;                return ret;            }            else            {                Logger.Error("Load {0} error", path);            }            return null;        }    }}